mapper.select:
SELECT * FROM user


        name LIKE CONCAT('%', #{name}, '%')


        AND gender = #{gender}


        AND create_time BETWEEN #{begin} AND #{end}


ORDER BY id
limit #{offset}, #{pageSize}

mapper.update:
update user
username = #{username}, password = #{password}, name = #{name}, gender = #{gender}, job = #{job}, image = #{image}, dept_id = #{deptId}, update_time = #{updateTime}
WHERE id = #{id}
